choose.when:
select ROUND((teen/sum(t3.teen+t3.twenty+t3.thirty+t3.forty)*100),1) 'teen', ROUND((twenty/sum(t3.teen+t3.twenty+t3.thirty+t3.forty)*100),1) 'twenty', ROUND((thirty/sum(t3.teen+t3.twenty+t3.thirty+t3.forty)*100),1) 'thirty', ROUND((forty/sum(t3.teen+t3.twenty+t3.thirty+t3.forty)*100),1) 'forty'
from(select COUNT((CASE WHEN t2.ages DIV 10 = 1 THEN 1 ELSE NULL END)) 'teen', COUNT((CASE WHEN t2.ages DIV 10 = 2 THEN 1 ELSE NULL END)) 'twenty', COUNT((CASE WHEN t2.ages DIV 10 = 3 THEN 1 ELSE NULL END)) 'thirty', COUNT((CASE WHEN t2.ages DIV 10 = 4 THEN 1 ELSE NULL END)) 'forty'
from (select (YEAR(now())-(MID(ssn,1,2)+(IF (MID(ssn, 8, 1) = '1' OR MID(ssn, 8, 1) = '2', 1900, 2000)))) AS ages From (select ssn from reservation r join member m on r.member_id = m.member_id where r.movie_seq=#{param.movie_seq})t1)t2)t3;
