jahun.main=(()=>{
	var init=(ctx)=>{
		jahun.session.init(ctx);
		jahun.template.mainUI();
		mainLogic();
	};
	var mainLogic=()=>{
		
		if(sessionStorage.getItem('member_id')==null){
			$('#loginBtn').text('로그인');
		}else{
			$('#loginBtn').text('로그아웃');
			$('#loginMsg').text(sessionStorage.getItem('member_id')+'님 환영합니다 !');
			$('#loginMsg').attr('style','font-size:1.5em');
			if(sessionStorage.getItem('member_img')!=null){
				$('#profile_img').attr('src',sessionStorage.getItem('member_img'));
			}
		}
		$('#goMovie').click(()=>{
			$('#content').empty();
			location.href=sessionStorage.getItem('ctx')+"/movieChart";
		});
		$('#goReservation').click(()=>{
			$('#content').empty();
			location.href=sessionStorage.getItem('ctx')+"/reservation";
		});
		
		$('#joinBtn').click(()=>{
			if(sessionStorage.getItem('member_id')!=null){
				alert('로그아웃을 먼저 해주세요');
				location.href=sessionStorage.getItem('ctx')+'/home';
			}else{
				$('#content').empty();
				location.href=sessionStorage.getItem('ctx')+"/join";
			}
		});
		
		$('#mycgvBtn').click(()=>{
			$('#content').empty();
			kyungchul.main.init(sessionStorage.getItem('ctx'));
		});
		$('#loginBtn').click(()=>{
			var loginText=$('#loginBtn').text();
			if(loginText=='로그인'){
				$('#content').empty();
				location.href=sessionStorage.getItem('ctx')+"/loginpage";
			}else{
				if(sessionStorage.getItem('member_img')==null){
					sessionStorage.removeItem('member_id');
					$('#loginBtn').text('로그인');
					$('#loginMsg').text('');
				}else{
					$.ajax({
			            url : sessionStorage.getItem('ctx')+'/delete/member',
			            method : 'POST',
			            data : JSON.stringify({
			               member_id : sessionStorage.getItem('member_id'),
			               pass : 'kakao'
			            }),
			            contentType : 'application/json',
			            success : d =>{
			            	sessionStorage.removeItem('member_id');
			            	sessionStorage.removeItem('member_img');
			            	$('#profile_img').removeAttr('src','');
			            	$('#loginBtn').text('로그인');
							$('#loginMsg').text('');
			            },
			            error : (x,s,m)=>{
			               alert('오류발생 : '+m);
			            }             
			         });
				}
			}
		});
		
		var posters=[
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79960/79960_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79952/79952_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79902/79902_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79949/79949_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79910/79910_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000080/80056/80056_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000080/80055/80055_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79793/79793_185.jpg',
			'http://img.cgv.co.kr/Movie/Thumbnail/Poster/000079/79978/79978_185.jpg'
			];
		var index=0;
		
		$('#prevBtn').click(()=>{
			index--;
			if(index==-1){
				index=8;
			}
			$('#mainPoster').attr("src",posters[index]);
			sessionStorage.setItem('movie_seq',index+1);
		});
		
		$('#nextBtn').click(()=>{
			index++;
			if(index==9){
				index=0;
			}
			$('#mainPoster').attr("src",posters[index]);
			sessionStorage.setItem('movie_seq',index+1);
		});
		
		
		
	};
	var goDetail=()=>{
		location.href=sessionStorage.getItem('ctx')+"/movieDetail";
	};
	return {
		init : init,
		mainLogic : mainLogic,
		goDetail : goDetail
	};
})();

jahun.session=
{
	init : function(ctx){
		sessionStorage.setItem('ctx',ctx);
		sessionStorage.setItem('js',ctx+"/resources/js");
		sessionStorage.setItem('css',ctx+"/resources/css");
		sessionStorage.setItem('img',ctx+"/resources/img");
	},
	getPath : function(x){
		return sessionStorage.getItem(x);
	}
}

